import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import Header from './components/Header';
import Footer from './components/Footer';
import StatusPanel from './components/StatusPanel';
import ObservationPanel from './components/ObservationPanel';
import GoalPanel from './components/GoalPanel';
import ControlPanel from './components/ControlPanel';
import Visualization3D from './components/Visualization3D';
import LogPanel from './components/LogPanel';

// API URL for backend communication
const API_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8080';

function App() {
  // Socket reference
  const socketRef = useRef(null);

  // State management
  const [status, setStatus] = useState({
    environment: false,
    model: false,
    controller: false,
    goal: false,
    running: false
  });

  const [loading, setLoading] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');
  const [statusType, setStatusType] = useState('');
  const [observationImage, setObservationImage] = useState('static/images/placeholder.png');
  const [goalImage, setGoalImage] = useState('static/images/placeholder.png');
  const [goalSet, setGoalSet] = useState(false);
  const [robotState, setRobotState] = useState({
    position: [0, 0, 0],
    gripper: 0
  });
  const [progress, setProgress] = useState(0);
  const [distance, setDistance] = useState('-');
  const [logEntries, setLogEntries] = useState([{
    timestamp: 'System initialized',
    message: 'Welcome to V-JEPA 2 Robot Control',
    type: 'info'
  }]);

  // Initialize Socket.IO connection
  useEffect(() => {
    // Connect with direct URL to avoid path confusion
    socketRef.current = io('http://localhost:8080', {
      transports: ['websocket', 'polling'],
      withCredentials: false,
      autoConnect: true
    });

    socketRef.current.on('connect', () => {
      logMessage('Connected to server');
      fetchStatus();
    });

    socketRef.current.on('disconnect', () => {
      logMessage('Disconnected from server', 'error');
      setStatus({
        environment: false,
        model: false,
        controller: false,
        goal: false,
        running: false
      });
    });

    socketRef.current.on('observation', (data) => {
      setObservationImage(`data:image/jpeg;base64,${data.image}`);
    });

    socketRef.current.on('control_result', (data) => {
      setStatus(prev => ({...prev, running: false}));

      if (data.success) {
        showStatusMessage(`Goal reached in ${data.steps} steps with distance ${data.final_distance.toFixed(4)}`, 'success');
        logMessage(`Goal reached in ${data.steps} steps (${data.time_taken.toFixed(2)}s)`);
      } else {
        showStatusMessage(`Failed to reach goal: ${data.reason}`, 'error');
        logMessage(`Failed to reach goal: ${data.reason}`, 'warning');
      }

      setProgress(100);
    });

    socketRef.current.on('control_progress', (data) => {
      const progressPercent = (data.step / data.max_steps) * 100;
      setProgress(progressPercent);
      setDistance(data.distance.toFixed(4));

      if (data.robot_state) {
        setRobotState({
          position: data.robot_state.position,
          gripper: data.robot_state.gripper
        });
      }
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  // API calls
  const fetchStatus = async () => {
    try {
      const response = await fetch(`${API_URL}/api/status`);
      const data = await response.json();
      updateStatus(data);
    } catch (error) {
      console.error('Error fetching status:', error);
      showStatusMessage('Failed to connect to server', 'error');
    }
  };

  const initializeSystem = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/initialize`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ gui: true }),
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('System initialized successfully', 'success');
        logMessage('System initialized successfully');
        fetchStatus();
      } else {
        showStatusMessage('Failed to initialize system', 'error');
        logMessage('Failed to initialize system', 'error');
      }
    } catch (error) {
      console.error('Error initializing system:', error);
      showStatusMessage('Error initializing system: ' + error.message, 'error');
      logMessage('Error initializing system: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const resetEnvironment = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/reset`, {
        method: 'POST',
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('Environment reset successfully', 'success');
        logMessage('Environment reset successfully');
        fetchStatus();
      } else {
        showStatusMessage('Failed to reset environment', 'error');
        logMessage('Failed to reset environment', 'error');
      }
    } catch (error) {
      console.error('Error resetting environment:', error);
      showStatusMessage('Error resetting environment: ' + error.message, 'error');
      logMessage('Error resetting environment: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const shutdownSystem = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/shutdown`, {
        method: 'POST',
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('System shutdown successfully', 'success');
        logMessage('System shutdown successfully');
        fetchStatus();
      } else {
        showStatusMessage('Failed to shutdown system', 'error');
        logMessage('Failed to shutdown system', 'error');
      }
    } catch (error) {
      console.error('Error shutting down system:', error);
      showStatusMessage('Error shutting down system: ' + error.message, 'error');
      logMessage('Error shutting down system: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const sendGoalImage = async (imageData) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/set_goal`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ image: imageData }),
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('Goal image set successfully', 'success');
        logMessage('Goal image set successfully');
        setGoalSet(true);
        fetchStatus();
      } else {
        showStatusMessage('Failed to set goal image', 'error');
        logMessage('Failed to set goal image', 'error');
      }
    } catch (error) {
      console.error('Error setting goal image:', error);
      showStatusMessage('Error setting goal image: ' + error.message, 'error');
      logMessage('Error setting goal image: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const runControlLoop = async () => {
    if (!status.environment || !status.model || !status.controller || !goalSet) {
      showStatusMessage('System not fully initialized or goal not set', 'error');
      return;
    }

    const planningHorizon = document.getElementById('planning-horizon').value;
    const distanceThreshold = document.getElementById('distance-threshold').value;

    setLoading(true);
    setStatus(prev => ({...prev, running: true}));

    try {
      const response = await fetch(`${API_URL}/api/run_control`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          planning_horizon: parseInt(planningHorizon),
          distance_threshold: parseFloat(distanceThreshold)
        }),
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('Control loop started', 'success');
        logMessage('Control loop started with planning horizon ' + planningHorizon + ' and distance threshold ' + distanceThreshold);
      } else {
        setStatus(prev => ({...prev, running: false}));
        showStatusMessage('Failed to start control loop: ' + data.error, 'error');
        logMessage('Failed to start control loop: ' + data.error, 'error');
      }
    } catch (error) {
      console.error('Error starting control loop:', error);
      setStatus(prev => ({...prev, running: false}));
      showStatusMessage('Error starting control loop: ' + error.message, 'error');
      logMessage('Error starting control loop: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const stopExecution = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/stop`, {
        method: 'POST',
      });

      const data = await response.json();
      if (data.success) {
        showStatusMessage('Execution stopped', 'success');
        logMessage('Execution stopped');
        setStatus(prev => ({...prev, running: false}));
      } else {
        showStatusMessage('Failed to stop execution', 'error');
        logMessage('Failed to stop execution', 'error');
      }
    } catch (error) {
      console.error('Error stopping execution:', error);
      showStatusMessage('Error stopping execution: ' + error.message, 'error');
      logMessage('Error stopping execution: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  // Helper functions
  const updateStatus = (data) => {
    setStatus({
      environment: data.environment_initialized,
      model: data.model_initialized,
      controller: data.controller_initialized,
      goal: data.goal_set,
      running: data.running
    });

    if (data.goal_set) {
      setGoalSet(true);
    }
  };

  const showStatusMessage = (message, type) => {
    setStatusMessage(message);
    setStatusType(type);
    setTimeout(() => {
      setStatusMessage('');
    }, 5000);
  };

  const logMessage = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLogEntries(prev => [
      {
        timestamp,
        message,
        type
      },
      ...prev
    ]);
  };

  const clearLog = () => {
    setLogEntries([{
      timestamp: 'System initialized',
      message: 'Welcome to V-JEPA 2 Robot Control',
      type: 'info'
    }]);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const imageData = event.target.result.split(',')[1];
        setGoalImage(`data:${file.type};base64,${imageData}`);
        setGoalImage(event.target.result);
        setGoalSet(true);

        // Send to server
        sendGoalImage(imageData);
      };
      reader.readAsDataURL(file);
    }
  };

  const captureCurrentView = () => {
    if (observationImage && observationImage !== 'static/images/placeholder.png') {
      const imageData = observationImage.split(',')[1];  // Get base64 part
      setGoalImage(observationImage);
      setGoalSet(true);

      // Send to server
      sendGoalImage(imageData);
    } else {
      showStatusMessage('No observation available to capture', 'error');
    }
  };

  return (
    <div className="App">
      <Header />

      <main>
        <div className="container">
          <StatusPanel 
            status={status} 
            loading={loading} 
            statusMessage={statusMessage} 
            statusType={statusType}
            onInitialize={initializeSystem}
            onReset={resetEnvironment}
            onShutdown={shutdownSystem}
          />

          <div className="views-container">
            <ObservationPanel 
              observationImage={observationImage} 
              robotState={robotState} 
            />

            <GoalPanel 
              goalImage={goalImage}
              goalSet={goalSet}
              onFileUpload={handleFileUpload}
              onCapture={captureCurrentView}
            />
          </div>

          <ControlPanel 
            status={status}
            progress={progress}
            distance={distance}
            onRun={runControlLoop}
            onStop={stopExecution}
          />

          <Visualization3D />

          <LogPanel 
            logEntries={logEntries}
            onClearLog={clearLog}
          />
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default App;
